  } else {
    var referenceName = tagName.substring(0, tagName.indexOf('-'));
    dataTableName = ewonName + '-' + referenceName + suffix;
  }
  try {
    var nameTest;
    /*
     * Attempting to access a thing in order to check if it exists.
     * The assignment just makes warnings go away.
     */
    nameTest = Things[dataTableName].name;
  } catch (err) {
    // a data table needs to be created
    var params = {
      name: dataTableName /* STRING */,
      description: undefined /* STRING */,
      sourceThingName: me.dataTableToCopy /* THINGNAME */,
      tags: undefined /* TAGS */,
    };
    // all of these are needed to create and setup a thing
    Resources['EntityServices'].CloneThing(params);
    Things[dataTableName].EnableThing();
    Things[dataTableName].RestartThing();
  }

  // returns an empty infotable row to be filled with values
  var rowValues = Things[dataTableName].CreateValues();
  rowValues.Name = tagName;
  rowValues.Value = tagValue;
  var id = Things[dataTableName].AddOrUpdateDataTableEntry({
    sourceType: undefined /* STRING */,
    values: rowValues /* INFOTABLE */,
    location: undefined /* LOCATION */,
    source: ewonName /* STRING */,
    tags: undefined,
  });
